import AddComment from './AddComment'

const Comment = ({
    singleComment, 
    activeComment, 
    toggleCommentForm, 
    setCommentsContent, 
    commentsContent, 
    updateScore,
    setActiveComment
}) => {

    const isActive = singleComment.id === activeComment
    const activate = () => {
        toggleCommentForm(singleComment.id)
    }

    const updateScoreClick = (e) => {
        let val = e.currentTarget.value
        updateScore(val,singleComment.id)
    }

    return (
    <div className="root-comment-master">
        <div className="root-comment white-bkg">
        <div className="score main-bkg">
          <button className="btn-reset btn no-bkg" value='plus' onClick={updateScoreClick}><img className="img" src={'./images/icon-plus.svg'} alt=""/></button>
          <span className="point clr-reply f-w-700">{singleComment.score}</span>
          <button className="btn-reset btn no-bkg" value='minus' onClick={updateScoreClick}><img className="img" src={'./images/icon-minus.svg'} alt=""/></button>
        </div>
        <div className="comm-meta-data">
          <img src={singleComment.user.image.png} alt="" className="avatar"/>
          <span className="nick nick-clr f-w-700">{singleComment.user.username}</span>
          <span className="uploaded-on clr-uploaded-on f-w-500">{singleComment.createdAt}</span>
        </div>
        <div className="action-row">
          
          <button className="clr-reply f-w-700 btn-reset no-bkg-gap" onClick={activate}><img src={'./images/icon-reply.svg'} alt=""/> Reply</button>
        </div>
        <div className="comment-body">
          <p className="actual-comment clr-uploaded-on f-w-500">
            {singleComment.replyingTo && <span className="replying-to">@{singleComment.replyingTo} </span>}
            {singleComment.content}</p>
        </div>
      </div>

      {isActive && (
        <AddComment 
        setCommentsContent={setCommentsContent} 
        commentsContent={commentsContent}
        actionType="reply"
        parentId={singleComment.parentId}
        commentId={singleComment.id}
        setActiveComment={setActiveComment}
        replyingTo={singleComment.user.username}
        />)}

      
    {singleComment.replies && singleComment.replies.length > 0 && (
        <div className="reply-comments">
      {singleComment.replies.map(singleComment => 
            <Comment key={singleComment.id} 
            singleComment={singleComment} 
            toggleCommentForm={toggleCommentForm} 
            activeComment={activeComment} 
            setCommentsContent={setCommentsContent} 
            commentsContent={commentsContent}
            setActiveComment={setActiveComment}
        />)} 
        </div> 
    )}
      
    </div>
    )
}

export default Comment;
