const newComment = (text, parentId) => {
    let comment = {
        id: 1,
        content: text,
        createdAt: new Date().toISOString,
        score:0,
        user: {
            image: { 
                png: "./images/avatars/image-amyrobson.png",
                webp: "./images/avatars/image-amyrobson.webp"
              },
              username: "amyrobson"
        },
        parentId:parentId
    }
    return comment
}

export default newComment
