import { useState } from 'react'
import newComment from './newComment'

const AddComment = ({setCommentsContent, commentsContent}) => {

const[text, setText] = useState([])
const add = (e) => {
    e.preventDefault()
    let comm = newComment(text)
    console.log(comm)
    console.log(commentsContent)
    let updatedCommentsContent = [...commentsContent,comm];
    setCommentsContent(updatedCommentsContent)
    setText('')
}

    return (
    <form onSubmit={add} className="add-comment white-bkg">
             <img className="img avatar comment-av" src="./images/avatars/image-maxblagun.png" alt=""/>
             <textarea className="textarea" name="comment" id="cmt" placeholder="Add a comment..." value={text} onChange={(e)=>setText(e.target.value)}></textarea>
             <button className="btn-reset blue-bkg clr-white send-btn">send</button>
    </form>
    )
}

export default AddComment;
