import AddComment from './AddComment'
import {useState} from 'react' 



const Comment = ({singleComment, activeComment, toggleCommentForm, setCommentsContent, commentsContent}) => {

    const isActive = singleComment.id === activeComment
    const activate = () => {
        toggleCommentForm(singleComment.id)
    }

    const[rerender,render] = useState(false)

    const updateScore = (e) => {
        let val = e.currentTarget.value
        let score = commentsContent.find(comment => comment.id === singleComment.id)
        score.score = val === 'plus'? score.score += 1 : score.score -+ 1
        setCommentsContent(commentsContent)
        render(!rerender)
    }

    return (
    <div class="root-comment-master">
        <div className="root-comment white-bkg">
        <div className="score main-bkg">
          <button className="btn-reset btn no-bkg" value='plus' onClick={updateScore}><img className="img" src={'./images/icon-plus.svg'} alt=""/></button>
          <span className="point clr-reply f-w-700">{singleComment.score}</span>
          <button className="btn-reset btn no-bkg" value='minus' onClick={updateScore}><img className="img" src={'./images/icon-minus.svg'} alt=""/></button>
        </div>
        <div className="comm-meta-data">
          <img src={singleComment.user.image.png} alt="" className="avatar"/>
          <span className="nick nick-clr f-w-700">{singleComment.user.username}</span>
          <span className="uploaded-on clr-uploaded-on f-w-500">{singleComment.createdAt}</span>
        </div>
        <div className="action-row">
          
          <btn className="clr-reply f-w-700" onClick={activate}><img src={'./images/icon-reply.svg'} alt=""/> Reply</btn>
        </div>
        <div className="comment-body">
          <p className="actual-comment clr-uploaded-on f-w-500">{singleComment.content}</p>
        </div>
      </div>
      {isActive && <AddComment/>}

      
      {singleComment.replies && singleComment.length > 0 && (
        <div class="reply-comments">
      {singleComment.replies.map(singleComment => <Comment key={singleComment.id} singleComment={singleComment} toggleCommentForm={toggleCommentForm} activeComment={activeComment}/>)} 
      </div> 
     )}
      
    </div>
    )
}

export default Comment;
