import {useState, useEffect} from 'react' 
import data from './data.json'
import Comment from './Comment'
import AddComment from './AddComment'



const Comments = () => {

    const[commentsContent, setCommentsContent] = useState([])
    const[activeComment, setActiveComment] = useState([])

    const toggleCommentForm = (id) => {
        setActiveComment(id)
    }

    useEffect(()=> {
        let defaultComments = [...data.comments]
        setCommentsContent(defaultComments)
    },[])

    const updateScore = (val,id) => {
        let updatedCommentsContent = [...commentsContent];
        console.log(updatedCommentsContent.score)
        let score = updatedCommentsContent.find(comment => comment.id === id)
        score.score = val === 'plus'? score.score += 1 : score.score -+ 1;
        setCommentsContent(updatedCommentsContent)
    }

    return (
        <main className="main main-bkg">
      <div className="sec-main">


    <div className='comments-container'>
        {commentsContent.map(singleComment => 
        <Comment key={singleComment.id} 
        singleComment={singleComment} 
        toggleCommentForm={toggleCommentForm} 
        activeComment={activeComment} 
        setCommentsContent={setCommentsContent} 
        commentsContent={commentsContent}
        updateScore={updateScore}
        setActiveComment={setActiveComment}
        />)}
    </div>
        <AddComment 
        setCommentsContent={setCommentsContent} 
        commentsContent={commentsContent} 
        actionType="newComment"
        parentId='null'
        setActiveComment={setActiveComment}
        />
    </div>
    </main>
    )
}


export default Comments;
