import {useState, useEffect} from 'react' 
import data from './data.json'
import Comment from './Comment'


const Comments = () => {

    const[commentsContent, setCommentsContent] = useState([])
    const[activeComment, setActiveComment] = useState([])

    const toggleCommentForm = (id) => {
        setActiveComment(id)
    }

    // useEffect(() => {
    //     Comments
    // },[activeComment])

    useEffect(()=> {
        let defaultComments = data.comments
        setCommentsContent(defaultComments)
    },[])

    return (
        <div className='comments-container'>
        {commentsContent.map(singleComment => <Comment key={singleComment.id} singleComment={singleComment} toggleCommentForm={toggleCommentForm} activeComment={activeComment}/>)}
           </div>
    )
}


export default Comments;
